class Solution:
    # 超出时间限制
    # def longestOnes(self, nums: List[int], k: int) -> int:
    #     n = len(nums)
    #     if  n == 1 : return 1
    #     if  k>= n : return  k
    #     i,j = 0,k
    #     save = 0
    #     while j<=n-1:
    #         if sum(nums[i:j+1])>=j-i+1-k:
    #             j += 1
    #         else:
    #             save = max(save,sum(nums[i:j])+k)
    #             i += 1
    #             j = i+k
    #     save = max(save, sum(nums[i:j]) + k)
    #     return save


    def longestOnes(self, nums: List[int], k: int) -> int:
        n = len(nums)
        res = 0
        left,right = 0,0
        zeros = 0
        while right<n:
            if nums[right] == 0:
                zeros += 1
            while zeros>k:
                if nums[left] == 0:
                    zeros -= 1
                left += 1
            res = max(res,right-left+1)
            right += 1
        return res
